/*Table structure for table `wp_posts` */

DROP TABLE IF EXISTS `wp_posts`;

CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8;

/*Data for the table `wp_posts` */

insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2013-07-25 14:19:07','2013-07-25 14:19:07','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','closed','open','','hello-world','','','2013-07-26 08:15:34','2013-07-26 08:15:34','',0,'http://localhost/wap/?p=1',0,'post','',1),(2,1,'2013-07-25 14:19:07','2013-07-25 14:19:07','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/wap/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','publish','open','open','','sample-page','','','2013-07-25 14:19:07','2013-07-25 14:19:07','',0,'http://localhost/wap/?page_id=2',0,'page','',0),(3,1,'2013-07-25 14:26:55','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2013-07-25 14:26:55','0000-00-00 00:00:00','',0,'http://localhost/wap/?p=3',0,'post','',0),(5,1,'2013-07-25 14:19:07','2013-07-25 14:19:07','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','inherit','open','open','','1-revision','','','2013-07-25 14:19:07','2013-07-25 14:19:07','',1,'http://localhost/wap/?p=5',0,'revision','',0),(6,1,'2013-07-25 15:50:23','2013-07-25 15:50:23','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','inherit','open','open','','1-autosave','','','2013-07-25 15:50:23','2013-07-25 15:50:23','',1,'http://localhost/wap/?p=6',0,'revision','',0),(7,1,'2013-07-25 15:59:07','2013-07-25 15:59:07','<div style=\"text-align: center;\">Xoay quanh <b>câu chuyện gay cấn</b>, khám phá <b>bí mật kinh hoàng</b> đã được giấu kín bấy lâu sẽ mang đến những giây phút hồi hộp, gay cấn cho các game thủ khi tham gia \"<b>Học viện Nữ sinh</b>\".</div>\r\n<div style=\"text-align: center;\"></div>\r\n<div style=\"text-align: center;\"><img alt=\"\" src=\"http://static.mwork.vn/thumbs/unsafe/120x0/i.imgur.com/pkeOUZS.png\" border=\"0\" /></div>\r\n<div style=\"text-align: center;\"></div>\r\n<div style=\"text-align: center;\">\r\n<div>Bắt đầu bằng việc cậu học sinh <b>Manoru</b> chuyển đến trường học mới trong mùa hè 2013. Do <b>nhầm lẫn giấy tờ</b>, cậu được chuyển đến Học viện Quý Tộc Duchannes - trường nữ sinh. Trong vòng vây của các nữ sinh xinh đẹp và cá tính, Manoru đột nhiên trở thành chàng hotboy được cả trường quan tâm, theo dõi.</div>\r\n<div></div>\r\n<div><img alt=\"\" src=\"http://static.mwork.vn/thumbs/unsafe/120x0/i.imgur.com/ZJKW0Bw.png\" border=\"0\" /></div>\r\n<div></div>\r\n<div>Tại đây, Manoru nhanh chóng làm quen với những nữ sinh xinh đẹp và cá tính nhất: Ran, Rei, Quý Mỹ..., khởi đầu cho những cuộc phiêu lưu mới.</div>\r\n<div><img alt=\"\" src=\"http://static.mwork.vn/thumbs/unsafe/120x0/i.imgur.com/jMdl8I9.png\" border=\"0\" /></div>\r\n<div></div>\r\n<div>Manoru cùng các bạn lần lượt khám phá những bí ẩn về nguồn gốc kỳ bí của Học viện Duchannes, những tội ác được sắp đặt cực kỳ công phu dần dần hé mở...</div>\r\n<div><img alt=\"\" src=\"http://static.mwork.vn/thumbs/unsafe/120x0/i.imgur.com/7kTcP86.png\" border=\"0\" /></div>\r\n</div>\r\n<div style=\"text-align: center;\"><b>Nhập vai</b> thành học sinh trường học viện quý tộc Duchannes, các bạn nhỏ sẽ nhanh chóng bị thu hút vào những câu chuyện bí ẩn. Chế độ đánh theo lượt nhẹ nhàng nhưng đầy trí tuệ tạo cảm giác hứng thú khi vượt qua các thử thách.</div>\r\n<div style=\"text-align: center;\"></div>\r\n<div style=\"text-align: center;\"><img alt=\"\" src=\"http://static.mwork.vn/thumbs/unsafe/120x0/i.imgur.com/zNCXyBd.png\" border=\"0\" /></div>\r\n<div style=\"text-align: center;\"></div>\r\n<div style=\"text-align: center;\">Ngoài ra, <b>đồ họa sắc nét</b> được thiết kế theo phong cách <b>hoạt hình dễ thương Nhật Bản</b>, hút hồn các game thủ Teen.</div>\r\n<div style=\"text-align: center;\"><img alt=\"\" src=\"http://static.mwork.vn/thumbs/unsafe/120x0/i.imgur.com/klQoGPs.png\" border=\"0\" /></div>\r\n<div style=\"text-align: center;\"></div>\r\n<div style=\"text-align: center;\">Hãy cùng Manoru đến \"<b>Học viện Nữ sinh</b>\", phiêu lưu mạo hiểm trong mùa hè này nhé!</div>\r\n<p style=\"text-align: center;\"></p>','Học viện Nữ sinh - Đại chiến mùa hè','','publish','closed','open','','hoc-vien-nu-sinh-dai-chien-mua-he','','','2013-07-26 08:15:34','2013-07-26 08:15:34','',0,'http://localhost/wap/?p=7',0,'post','',0),(8,1,'2013-07-25 15:57:45','2013-07-25 15:57:45','<div style=\"text-align: center;\">Xoay quanh <b>câu chuyện gay cấn</b>, khám phá <b>bí mật kinh hoàng</b> đã được giấu kín bấy lâu sẽ mang đến những giây phút hồi hộp, gay cấn cho các game thủ khi tham gia \"<b>Học viện Nữ sinh</b>\".</div>\n<div style=\"text-align: center;\"></div>\n<div style=\"text-align: center;\"><img alt=\"\" src=\"http://static.mwork.vn/thumbs/unsafe/120x0/i.imgur.com/pkeOUZS.png\" border=\"0\" /></div>\n<div 